const ticket = $json;
const highPriorityKeywords = ['urgent','critical','down','broken','emergency'];
const mediumPriorityKeywords = ['issue','problem','bug','error'];
const text = ((ticket.subject||'')+' '+(ticket.description||'')).toLowerCase();
let priority='low';
if (highPriorityKeywords.some(k=>text.includes(k))) priority='high';
else if (mediumPriorityKeywords.some(k=>text.includes(k))) priority='medium';
let category='general';
if (text.includes('login')||text.includes('password')) category='authentication';
else if (text.includes('billing')||text.includes('payment')) category='billing';
else if (text.includes('technical')||text.includes('api')) category='technical';
return { json: { ...ticket, priority, category, ticket_id: 'TKT-'+Date.now(), created_at: new Date().toISOString() } };
